    private void cbEstadoItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbEstadoItemStateChanged
        CidadeDAO cidadeDaoInstancia = new CidadeDAO();
        Object selected = cbEstado.getSelectedItem();
        String estado = (selected != null && !selected.toString().equals("Escolher opção")) ? selected.toString() : null;

        if (estado != null) {
            // Extrai o número antes do " - "
            String idEstadoStr = estado.split(" - ")[0].trim();
            int idEstado = Integer.parseInt(idEstadoStr);

            List<Cidade> filteredCidades = cidadeDaoInstancia.buscarPorEstado(idEstado);
            cbCidade.removeAllItems();

            cbCidade.addItem("Escolher opção");
            for (Cidade cidade : filteredCidades) {
                cbCidade.addItem(cidade.getId() + " - " + cidade.getNome());
            }
        }
    }//GEN-LAST:event_cbEstadoItemStateChanged

