    private void btAplicarFiltrosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btAplicarFiltrosActionPerformed
        MessagesAlert messagesAlert = new MessagesAlert();
        try {
            String cep = ftfCep.getText().trim();

            // Remove caracteres não numéricos para checar se o campo está "vazio de verdade"
            cep = cep.replaceAll("[^\\d]", "");
            if (cep.isEmpty()) {
                cep = null;
            }

            // Número (com validação segura)
            Integer numero = null;
            String textoNumero = tfNumero.getText().trim();
            if (!textoNumero.isEmpty()) {
                try {
                    numero = Integer.valueOf(textoNumero);
                } catch (NumberFormatException e) {
                    messagesAlert.showErrorMessage("Número inválido no endereço.");
                    return;
                }
            }

            // Cidade (ID válido a partir da comboBox)
            Integer cidadeId = null;
            if (cbCidade.getSelectedIndex() > 0) {
                cidadeId = Integer.valueOf(cbCidade.getSelectedItem().toString().split(" - ")[0]);
            }

            // Criar VO do Endereço com dados limpos
            EnderecoVO enderecoVO = new EnderecoVO(
                    tfRua.getText().trim(),
                    numero,
                    tfBairro.getText().trim(),
                    cidadeId,
                    cep,
                    tfComplemento.getText().trim()
            );

            System.out.println("ENDERECOOOO" + enderecoVO.toString());

            // Criar VO de Responsável
            ResponsavelVO responsavelVO = new ResponsavelVO(
                    tfResponsavel.getText(),
                    null, null, null, null, null, null, null
            );

            // Criar VO de Paciente para Filtro
            PacienteVO filtro = new PacienteVO(
                    null, // id
                    tfPaciente.getText(), // nome
                    cbGenero.getSelectedItem().toString().equals("Escolher opção") ? null : cbGenero.getSelectedItem().toString(),
                    null, // celularContato
                    null, // celular
                    tfPaciente.getText(), // paciente
                    tfData.getText(), // dataNascimento
                    null, // dataInscricao
                    cbInstrucao.getSelectedItem().toString().equals("Escolher opção") ? null : cbInstrucao.getSelectedItem().toString(),
                    tfProfissao.getText(),
                    cbEstadoCivil.getSelectedItem().toString().equals("Escolher opção") ? null : cbEstadoCivil.getSelectedItem().toString(),
                    cbRacaCorEtnia.getSelectedItem().toString().equals("Escolher opção") ? null : cbRacaCorEtnia.getSelectedItem().toString(),
                    cbOrientacao.getSelectedItem().toString().equals("Escolher opção") ? null : cbOrientacao.getSelectedItem().toString(),
                    cbNacionalidade.getSelectedIndex() == 0 ? null : Integer.parseInt(cbNacionalidade.getSelectedItem().toString().split(" - ")[0]),
                    null, // disponibilidade
                    cbEstagiario.getSelectedIndex() == 0 ? null : Integer.parseInt(cbEstagiario.getSelectedItem().toString().split(" - ")[0]),
                    cbOrientador.getSelectedIndex() == 0 ? null : Integer.parseInt(cbOrientador.getSelectedItem().toString().split(" - ")[0]),
                    enderecoVO != null ? enderecoVO.toEntity() : null,
                    responsavelVO != null ? responsavelVO.toEntity() : null,
                    cbAtendido.isSelected(),
                    cbAtivo.isSelected(),
                    null // grupo
            );

            System.out.println("Visao.Telas.FormBuscaAvancadaPaciente.btAplicarFiltrosActionPerformed()");
            System.out.println(filtro.toString());

            // Buscar via RN
            PacienteRN pacienteRN = new PacienteRN();

            List<Paciente> pacientesFiltrados = pacienteRN.filtrarPacientes(filtro);

            System.out.println("Visao.Telas.FormBuscaAvancadaPaciente.btAplicarFiltrosActionPerformed()");
            System.out.println(Arrays.deepToString(pacientesFiltrados.toArray()));

            FormManager.showForm(new TableBuscaAvancadaPaciente(pacientesFiltrados, obterColunasDinamicas()));
        } catch (Exception e) {
            messagesAlert.showErrorMessage("Erro ao aplicar filtros: " + e.getMessage());
        }
    }//GEN-LAST:event_btAplicarFiltrosActionPerformed
    public List<String> obterColunasDinamicas() {
        List<String> colunasSelecionadas = new ArrayList<>();

        colunasSelecionadas.add("#");
        colunasSelecionadas.add("ID");

        if (!tfPaciente.getText().trim().isEmpty()) {
            colunasSelecionadas.add("Nome");
        }

        if (cbGenero.getSelectedIndex() > 0) {
            String genero = cbGenero.getSelectedItem().toString().trim();
            if (!genero.equalsIgnoreCase("null")) {
                colunasSelecionadas.add("Gênero");
            }
        }

        if (cbEstadoCivil.getSelectedIndex() > 0) {
            String ec = cbEstadoCivil.getSelectedItem().toString().trim();
            if (!ec.equalsIgnoreCase("null")) {
                colunasSelecionadas.add("Estado Civil");
            }
        }

        if (cbInstrucao.getSelectedIndex() > 0) {
            String instrucao = cbInstrucao.getSelectedItem().toString().trim();
            if (!instrucao.equalsIgnoreCase("null")) {
                colunasSelecionadas.add("Grau de Instrução");
            }
        }

        if (!tfProfissao.getText().trim().isEmpty()) {
            colunasSelecionadas.add("Profissão");
        }

        if (cbRacaCorEtnia.getSelectedIndex() > 0) {
            String raca = cbRacaCorEtnia.getSelectedItem().toString().trim();
            if (!raca.equalsIgnoreCase("null")) {
                colunasSelecionadas.add("Raça/Cor/Etnia");
            }
        }

        if (cbOrientacao.getSelectedIndex() > 0) {
            String orientacao = cbOrientacao.getSelectedItem().toString().trim();
            if (!orientacao.equalsIgnoreCase("null")) {
                colunasSelecionadas.add("Orientação Sexual");
            }
        }

        if (cbNacionalidade.getSelectedIndex() > 0) {
            colunasSelecionadas.add("Nacionalidade");
        }

        if (cbEstagiario.getSelectedIndex() > 0) {
            colunasSelecionadas.add("Estagiário");
        }

        if (cbOrientador.getSelectedIndex() > 0) {
            colunasSelecionadas.add("Orientador");
        }

        if (!tfData.getText().trim().isEmpty()) {
            colunasSelecionadas.add("Data de Nascimento");
        }

        if (cbAtivo.isSelected()) {
            colunasSelecionadas.add("Ativo");
        }

        if (cbAtendido.isSelected()) {
            colunasSelecionadas.add("Atendido");
        }

        String cep = ftfCep.getText().replaceAll("[^\\d]", "");
        if (!cep.isEmpty()) {
            colunasSelecionadas.add("CEP");
        }

        if (!tfRua.getText().trim().isEmpty()) {
            colunasSelecionadas.add("Endereço");
        }
        if (!tfNumero.getText().trim().isEmpty()) {
            colunasSelecionadas.add("Numero");
        }
        if (!tfBairro.getText().trim().isEmpty()) {
            colunasSelecionadas.add("Bairro");
        }
        if (!tfComplemento.getText().trim().isEmpty()) {
            colunasSelecionadas.add("Complemento");
        }

        if (cbCidade.getSelectedIndex() > 0) {
            colunasSelecionadas.add("Cidade");
        }
        if (cbEstado.getSelectedIndex() > 0) {
            colunasSelecionadas.add("Estado");
        }

        return colunasSelecionadas;
    }

    public void inserirDadosFormulario() {
        PaisDAO paisDaoInstancia = new PaisDAO();
        CidadeDAO cidadeDaoInstancia = new CidadeDAO();
        EstadoDAO estadoDaoInstancia = new EstadoDAO();
        OrientadorRN orientadorRn = new OrientadorRN();
        EstagiarioRN estagiarioRn = new EstagiarioRN();

        List<Pais> todosPaises = paisDaoInstancia.buscarTodos();
        List<Cidade> todasCidades = cidadeDaoInstancia.buscarTodos();
        List<Estado> todosEstados = estadoDaoInstancia.buscarTodos();

        List<OrientadorVO> todosOrientadores = orientadorRn.listarOrientadores();
        List<EstagiarioVO> todosEstagiarios = estagiarioRn.listarEstagiarios();

//        cbNacionalidade.removeAllItems();
//        cbEstado.removeAllItems();
//        cbCidade.removeAllItems();
//
//        cbEstagiario.removeAllItems();
//        cbOrientador.removeAllItems();
        // Add Paises to Nacionalidade ComboBox
        for (Pais pais : todosPaises) {
            cbNacionalidade.addItem(String.valueOf(pais.getId()) + " - " + pais.getNome());
        }

        // Add Estados to Estado ComboBox
        for (Estado estado : todosEstados) {
            cbEstado.addItem(String.valueOf(estado.getId()) + " - " + estado.getNome());
        }

        // Add Cidades to Cidade ComboBox
        for (Cidade cidade : todasCidades) {
            cbCidade.addItem(String.valueOf(cidade.getId()) + " - " + cidade.getNome());
        }

        // Add Orientadores to Orientador ComboBox
        for (OrientadorVO orientador : todosOrientadores) {
            cbOrientador.addItem(String.valueOf(orientador.getId()) + " - " + orientador.getNomeCompleto());
        }

        // Add Estagiarios to Estagiario ComboBox
        for (EstagiarioVO estagiario : todosEstagiarios) {
            cbEstagiario.addItem(String.valueOf(estagiario.getId()) + " - " + estagiario.getNomeCompleto());
        }

    }

